import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import CategoryList from "./components/CategoryList";
import MovieList from "./components/MovieList";
import "./styles.css";
import "./App.css";
import cinecaptionTTF from "./fonts/cinecaption226.ttf";
import playfairDisplayTTF from "./fonts/PlayfairDisplay-VariableFont_wght.ttf"; // ← 追加

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("upcoming");

  useEffect(() => {
  const style = document.createElement("style");
  style.textContent = `
    @font-face {
      font-family: "Cinecaption";
      src: url(${cinecaptionTTF}) format("truetype");
      font-weight: normal;
      font-style: normal;
    }
    @font-face {
      font-family: "Playfair Display";
      src: url(${playfairDisplayTTF}) format("truetype");
      font-weight: normal;
      font-style: normal;
    }
  `;
  document.head.appendChild(style);

  return () => {
    document.head.removeChild(style);
  };
}, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    console.log("検索されたタイトル:", query);
  };

  return (
    <div className="App">
      <Header />
      <SearchBar onSearch={handleSearch} />
      <CategoryList
        selectedCategory={selectedCategory}
        onSelectCategory={setSelectedCategory}
      />
      <MovieList category={selectedCategory} query={searchQuery} />
    </div>
  );
}

export default App;
