import React, { useState, useEffect } from "react";
import Modal from "./Modal";
import ScaryForm from "./ScaryForm";
import styles from "./MovieModal.module.css";

const API_KEY = "9c5b6fe18f36543b858effdaf87e44e0";

const MovieModal = ({ isOpen, onClose, movieId, title }) => {
  const [movieDetails, setMovieDetails] = useState(null);
  const [scaryItems, setScaryItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!isOpen) return;

    const fetchMovieDetails = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch(
          `https://api.themoviedb.org/3/movie/${movieId}?api_key=${API_KEY}&language=ja-JP`
        );
        if (!res.ok) throw new Error(`ステータスコード ${res.status}`);
        const data = await res.json();
        setMovieDetails(data);
      } catch (e) {
        setError("映画情報の取得に失敗しました。");
      } finally {
        setLoading(false);
      }
    };
    fetchMovieDetails();
  }, [isOpen, movieId]);

  const handleAddScaryItem = (newItem) => {
    setScaryItems((prev) => [...prev, newItem]);
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={title}>
      <div className={styles.modalContent}>
        {loading && <p className={styles.status}>読み込み中...</p>}
        {error && <p className={styles.error}>{error}</p>}
        {!loading && !error && movieDetails && (
          <div>
            <div className={styles.detailWrapper}>
              <img
                src={
                  movieDetails.poster_path
                    ? `https://image.tmdb.org/t/p/w300${movieDetails.poster_path}`
                    : "https://via.placeholder.com/300x450?text=No+Image"
                }
                alt={movieDetails.title}
                className={styles.detailPoster}
              />
              <div className={styles.detailText}>
                <p><strong>公開日:</strong> {movieDetails.release_date || "不明"}</p>
                <p>
                  <strong>ジャンル:</strong>{" "}
                  {movieDetails.genres && movieDetails.genres.length > 0
                    ? movieDetails.genres.map(g => g.name).join(", ")
                    : "なし"}
                </p>
                <p><strong>評価:</strong> {movieDetails.vote_average || "なし"}</p>
                <p><strong>あらすじ:</strong> {movieDetails.overview || "なし"}</p>
                <p><strong>上映時間:</strong> {movieDetails.runtime ? `${movieDetails.runtime}分` : "なし"}</p>
              </div>
            </div>

            <h3>登録された恐怖要素</h3>
            {scaryItems.length === 0 ? (
              <p>登録なし</p>
            ) : (
              <ul className={styles.scaryList}>
                {scaryItems.map((item, i) => (
                  <li key={i}>
                    {item.types.join(", ")} — {item.detail} {item.time && `(${item.time})`}
                  </li>
                ))}
              </ul>
            )}

            <ScaryForm onAdd={handleAddScaryItem} />
          </div>
        )}
      </div>
    </Modal>
  );
};

export default MovieModal;
