import React, { useEffect, useState } from "react";

const MovieList = ({ category, query }) => {
  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8); // ✅ 最初の表示数
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  const fetchMovies = async () => {
    console.log("選択されたカテゴリー:", category); // ✅ カテゴリー確認

    let url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`
      : `https://api.themoviedb.org/3/movie/${category}?api_key=${API_KEY}&language=ja-JP`;

    console.log("リクエストURL:", url); // ✅ APIに送るURL確認

    try {
      const response = await fetch(url);

      if (!response.ok) {
        console.error(`APIリクエスト失敗: ステータスコード ${response.status}`);
        setMovies([]);
        return;
      }

      const data = await response.json();
      console.log("APIレスポンス:", data);

      setMovies(data.results || []); // ✅ `undefined` の場合は空配列にする
    } catch (error) {
     console.error("映画データの取得に失敗しました:", error);
    setMovies([]); // ❌ `data.results || []` は不要 → `catch` 内では `data` は存在しない
  }
};
  useEffect(() => {
    fetchMovies();
  }, [category, query]);

  useEffect(() => {
    if (movies.length > 0) {
      console.log("取得した映画データ:", movies); // ✅ `movies` の更新時に確認
    } else {
      console.warn("映画データが空です。リクエストURLやAPIキーを確認してください！");
    }
  }, [movies]);

 return (
  <div className="movie-list">
    {movies?.length > 0 ? ( // ✅ `movies?.length > 0` で安全にチェック
      movies.slice(0, visibleCount).map((movie) => ( 
        <div key={movie.id} className="movie-card">
          <img src={movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` : "https://via.placeholder.com/500"} alt={movie.title} />
          <h3>{movie.title}</h3>
          <p>⭐ {movie.vote_average}</p>
        </div>
      ))
    ) : (
      <p>⚠️ 映画データが取得できませんでした。APIキーやカテゴリーを確認してください。</p> // ✅ エラー時の表示
    )}
  </div>
);
};

export default MovieList;
