import React, { useState } from "react";
import styles from "./Footer.module.css";
import Modal from "./Modal";

const Footer = () => {
  // ここに状態や関数の定義が必要（省略されているようですが、必ず定義してください）
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState({ title: "", body: "" });

  const openModal = (title, body) => {
    setModalContent({ title, body });
    setModalOpen(true);
  };

  const closeModal = () => setModalOpen(false);

  // --- 以下省略していたモーダル用の本文などはここに入れてください ---

  return (
    <footer className={styles.footer}>
      <nav className={styles.nav}>
        {/* 省略 */}
      </nav>

      <div className={styles.copyRight}>
        <small>© 2023 ファビアチェック All Rights Reserved.</small>
        <br />
        <small>
          This product uses the TMDB API but is not endorsed or certified by TMDB.
        </small>

        {/* ここにTMDBロゴをReactコンポーネントとして表示 */}
        <div className={styles.tmdbLogo}>
          <div className={styles.tmdbLogo}>
  <img src="/TMDB.svg" alt="TMDB Logo" />
</div>
        </div>
      </div>

      <Modal isOpen={modalOpen} onClose={closeModal} title={modalContent.title}>
        <div style={{ textAlign: "left" }}>{modalContent.body}</div>
      </Modal>
    </footer>
  );
};

export default Footer;
