import React, { useState } from "react";
import Modal from "./Modal";
import ScaryForm from "./ScaryForm";

const MoviePoster = ({ posterUrl, title, overview }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [scaryItems, setScaryItems] = useState([]);

  const handleAddScaryItem = (newItem) => {
    setScaryItems([...scaryItems, newItem]);
  };

  return (
    <>
    <img
  src={posterUrl}
  alt={title}
  style={{ cursor: "pointer", maxWidth: "200px" }}
  onClick={() => {
    alert("ポスタークリックされたよ！");
    setModalOpen(true);
  }}
/>
      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)} title={title}>
        <p>{overview || "無し"}</p>

        <h3>登録された恐怖要素</h3>
        {scaryItems.length === 0 ? (
          <p>登録なし</p>
        ) : (
          <ul>
            {scaryItems.map((item, i) => (
              <li key={i}>
                {item.types.join(", ")} — {item.detail} {item.time && `(${item.time})`}
              </li>
            ))}
          </ul>
        )}

        <ScaryForm onAdd={handleAddScaryItem} />
      </Modal>
    </>
  );
};

export default MoviePoster;
