      console.log("‚úÖ API„É¨„Çπ„Éù„É≥„Çπ„ÅÆË©≥Á¥∞:", JSON.stringify(data, null, 2));
      const movieData = data.results?.length ? data.results : [];
      setMovies(movieData);
    } catch (error) {
      console.error("‚ùå Êò†Áîª„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
