import React, { useState } from "react";
import styles from "./Footer.module.css";
import Modal from "./Modal";

const Footer = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState({ title: "", body: "" });

  const openModal = (title, body) => {
    setModalContent({ title, body });
    setModalOpen(true);
  };

  const closeModal = () => setModalOpen(false);

  return (
    <footer className={styles.footer}>
      <nav className={styles.nav}>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal(
              "サイト説明",
              "「ファビアチェック」は、ファビア（恐怖症）を持つ方が映画を選ぶ際に、事前に恐怖要素を確認できるサイトです。様々な恐怖症（高所恐怖症、閉所恐怖症、蜘蛛恐怖症など）を持つ方が、安心して映画を楽しめるよう、映画に含まれる可能性のある恐怖要素を表示しています。このサイトは皆様からの情報提供によって成り立っています。映画の恐怖要素について知っていることがあれば、ぜひ情報を追加してください。"
            );
          }}
        >
          サイト説明
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("免責事項", "ここに免責事項の内容を書く");
          }}
        >
          免責事項
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("プライバシーポリシー", "ここにプライバシーポリシーの内容を書く");
          }}
        >
          プライバシーポリシー
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("運営者情報", "ここに運営者情報の内容を書く");
          }}
        >
          運営者情報
        </a>
        <a
          href="https://same-sunflower-d44.notion.site/20d0d0e1f57b80bd9af2c6e925aa0c20"
          target="_blank"
          rel="noopener noreferrer"
        >
          お問い合わせ
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("投稿ガイドライン", "ここに投稿ガイドラインの内容を書く");
          }}
        >
          投稿ガイドライン
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("FAQ", "ここにFAQの内容を書く");
          }}
        >
          FAQ
        </a>
      </nav>

      <div className={styles.copyRight}>
        <small>© 2023 ファビアチェック All Rights Reserved.</small>
        <br />
        <small>This product uses the TMDB API but is not endorsed or certified by TMDB.</small>
      </div>

      <div className={styles.tmdbLogo}>
        {/* TMDBロゴの画像などあればここに */}
      </div>

      <Modal isOpen={modalOpen} onClose={closeModal} title={modalContent.title}>
        <p>{modalContent.body}</p>
      </Modal>
    </footer>
  );
};

export default Footer;
