import React, { useEffect, useState } from "react";

const MovieList = ({ category, query }) => {
  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8); // 最初の表示数
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  const fetchMovies = async () => {
    let url = "";
    if (query) {
      url = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`;
    } else {
      const categoryMap = {
        upcoming: "upcoming",
        now_playing: "now_playing",
        popular: "popular",
        top_rated: "top_rated",
      };
      url = `https://api.themoviedb.org/3/movie/${categoryMap[category]}?api_key=${API_KEY}&language=ja-JP`;
    }

    try {
      const response = await fetch(url);
      const data = await response.json();
      setMovies(data.results);
    } catch (error) {
      console.error("映画データの取得に失敗しました", error);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [category, query]);

  return (
    <div className="movie-list">
      {movies.slice(0, visibleCount).map((movie) => (
        <div key={movie.id} className="movie-card">
          <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
          <h3>{movie.title}</h3>
          <p>⭐ {movie.vote_average}</p>
        </div>
      ))}
      {visibleCount < movies.length && (
        <button className="load-more" onClick={() => setVisibleCount(visibleCount + 8)}>
          もっと見る
        </button>
      )}
    </div>
  );
};

export default MovieList;
