import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import CategoryList from "./components/CategoryList";
import MovieList from "./components/MovieList";
import LoadMoreButton from "./components/LoadMoreButton";
import Footer from "./components/Footer";
import MovieModal from "./components/MovieModal";
import cinecaptionTTF from "./fonts/cinecaption226.ttf";
import playfairDisplayTTF from "./fonts/PlayfairDisplay-VariableFont_wght.ttf";
import "./styles.css";
import "./App.css";

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("upcoming");
  const [visibleCount, setVisibleCount] = useState(8);

  // モーダル管理のstate
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMovieId, setModalMovieId] = useState(null);
  const [modalTitle, setModalTitle] = useState("");

  useEffect(() => {
    const style = document.createElement("style");
    style.textContent = `
      @font-face {
        font-family: 'Cinecaption';
        src: url(${cinecaptionTTF}) format("truetype");
        font-weight: normal;
        font-style: normal;
      }
      @font-face {
        font-family: 'Playfair Display';
        src: url(${playfairDisplayTTF}) format("truetype");
        font-weight: normal;
        font-style: normal;
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    setVisibleCount(8);
  };

  const handleCategoryChange = (newCategory) => {
    setSelectedCategory(newCategory);
    setSearchQuery("");
    setVisibleCount(8);
  };

  const handleLoadMore = () => {
    setVisibleCount((prevCount) => prevCount + 8);
  };

  const openModalWithMovie = (id, title) => {
    setModalMovieId(id);
    setModalTitle(title);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalMovieId(null);
    setModalTitle("");
  };

  // 恐怖要素の登録用関数（MovieModalから呼ばれる）
  const handleAddFearReport = async (report) => {
    try {
      const res = await fetch("/fear_reports", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(report),
      });
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const json = await res.json();
      alert(json.message || "送信が成功しました！");
      // 必要ならここでモーダルを閉じる
      closeModal();
    } catch (error) {
      alert("送信に失敗しました");
      console.error(error);
    }
  };

  return (
    <div className="App">
      <Header />
      <SearchBar onSearch={handleSearch} />
      <CategoryList
        selectedCategory={selectedCategory}
        onSelectCategory={handleCategoryChange}
      />
      <MovieList
        category={selectedCategory}
        query={searchQuery}
        visibleCount={visibleCount}
        onMovieClick={openModalWithMovie}
      />
      <LoadMoreButton onClick={handleLoadMore} />
      <Footer />

      {/* モーダルに恐怖要素登録関数を渡す */}
      <MovieModal
        isOpen={modalOpen}
        onClose={closeModal}
        movieId={modalMovieId}
        title={modalTitle}
        onAddFearReport={handleAddFearReport}
      />
    </div>
  );
}

export default App;
