import React, { useEffect, useState } from "react";

const MovieList = ({ category, query }) => {
  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8); // ‚úÖ ÊúÄÂàù„ÅÆË°®Á§∫Êï∞
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  const fetchMovies = async () => {
    console.log("ÈÅ∏Êäû„Åï„Çå„Åü„Ç´„ÉÜ„Ç¥„É™„Éº:", category); // ‚úÖ „Ç´„ÉÜ„Ç¥„É™„ÉºÁ¢∫Ë™ç

    let url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`
      : `https://api.themoviedb.org/3/movie/${category}?api_key=${API_KEY}&language=ja-JP`;

    console.log("„É™„ÇØ„Ç®„Çπ„ÉàURL:", url); // ‚úÖ API„Å´ÈÄÅ„ÇãURLÁ¢∫Ë™ç

    try {
      const response = await fetch(url);

      if (!response.ok) {
        console.error(`API„É™„ÇØ„Ç®„Çπ„ÉàÂ§±Êïó: „Çπ„ÉÜ„Éº„Çø„Çπ„Ç≥„Éº„Éâ ${response.status}`);
        setMovies([]);
        return;
      }

      const data = await response.json();
      console.log("API„É¨„Çπ„Éù„É≥„Çπ:", data);

      setMovies(data.results || []); // ‚úÖ `undefined` „ÅÆÂ†¥Âêà„ÅØÁ©∫ÈÖçÂàó„Å´„Åô„Çã
    } catch (error) {
     console.error("Êò†Áîª„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
    setMovies([]); // ‚ùå `data.results || []` „ÅØ‰∏çË¶Å ‚Üí `catch` ÂÜÖ„Åß„ÅØ `data` „ÅØÂ≠òÂú®„Åó„Å™„ÅÑ
  }
};
  useEffect(() => {
    console.log("üé¨ useEffect ÂÆüË°å: category =", category, ", query =", query);
    fetchMovies();
  }, [category, query]);

  useEffect(() => {
    if (movies.length > 0) {
      console.log("ÂèñÂæó„Åó„ÅüÊò†Áîª„Éá„Éº„Çø:", movies); // ‚úÖ `movies` „ÅÆÊõ¥Êñ∞ÊôÇ„Å´Á¢∫Ë™ç
    } else {
      console.warn("Êò†Áîª„Éá„Éº„Çø„ÅåÁ©∫„Åß„Åô„ÄÇ„É™„ÇØ„Ç®„Çπ„ÉàURL„ÇÑAPI„Ç≠„Éº„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑÔºÅ");
    }
  }, [movies]);

 return (
  <div className="movie-list">
    {movies?.length > 0 ? ( // ‚úÖ `movies?.length > 0` „ÅßÂÆâÂÖ®„Å´„ÉÅ„Çß„ÉÉ„ÇØ
      movies.slice(0, visibleCount).map((movie) => ( 
        <div key={movie.id} className="movie-card">
          <img src={movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` : "https://via.placeholder.com/500"} alt={movie.title} />
          <h3>{movie.title}</h3>
          <p>‚≠ê {movie.vote_average}</p>
        </div>
      ))
    ) : (
      <p>‚ö†Ô∏è Êò†Áîª„Éá„Éº„Çø„ÅåÂèñÂæó„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇAPI„Ç≠„Éº„ÇÑ„Ç´„ÉÜ„Ç¥„É™„Éº„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ</p> // ‚úÖ „Ç®„É©„ÉºÊôÇ„ÅÆË°®Á§∫
    )}
  </div>
);
};

export default MovieList;
