  const [time, setTime] = useState("");

  const toggleFear = (fear) => {
    if (selectedFears.includes(fear)) {
      setSelectedFears(selectedFears.filter(f => f !== fear));
    } else {
      setSelectedFears([...selectedFears, fear]);
