      <input
        type="text"
        placeholder="„Åù„ÅÆ‰ªñÔºàËá™Áî±Ë®òËø∞Ôºâ"
        value={otherText}
        onChange={e => setOtherText(e.target.value)}
        className={styles.inputText}
      />
      <input
        type="text"
        placeholder="Ë©≥Á¥∞ÔºàÂøÖÈ†àÔºâ"
        value={detail}
        onChange={e => setDetail(e.target.value)}
        required
        className={styles.inputText}
      />
      <input
        type="text"
        placeholder="Âá∫ÁèæÊôÇÈñìÔºà‰ªªÊÑèÔºâ ‰æã: 01:23"
