import React, { useState } from "react";
import styles from "./Footer.module.css";
import Modal from "./Modal";

const Footer = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState({ title: "", body: "" });

  const openModal = (title, body) => {
    setModalContent({ title, body });
    setModalOpen(true);
  };

  const closeModal = () => setModalOpen(false);

  // 各モーダルの本文を定義
  const siteDescription = (
    <>
      「ファビアチェック」は、ファビア（恐怖症）を持つ方が映画を選ぶ際に、事前に恐怖要素を確認できるサイトです。<br />
      様々な恐怖症（高所恐怖症、閉所恐怖症、蜘蛛恐怖症など）を持つ方が、安心して映画を楽しめるよう、映画に含まれる可能性のある恐怖要素を表示しています。<br />
      このサイトは皆様からの情報提供によって成り立っています。映画の恐怖要素について知っていることがあれば、ぜひ情報を追加してください。
    </>
  );

  const disclaimer = (
    <>
      ・当サイトに掲載されている恐怖要素の情報は、ユーザーからの投稿に基づいています。情報の正確性は保証できませんので、あくまで参考程度にご利用ください。<br />
      ・投稿内容は運営の判断で予告なく修正・削除する場合があります。<br />
      ・映画の恐怖要素を説明する過程で、ネタバレを含む可能性があります。映画の内容を知りたくない方はご注意ください。<br />
      ・当サイトの利用によって生じたいかなる損害についても、運営者は責任を負いません。
    </>
  );

  const privacyPolicy = (
    <>
      1. 収集する情報<br />
      当サイトでは、サイト改善のために匿名の利用統計情報を収集することがあります。また、投稿機能を利用する際に入力された情報を保存します。<br /><br />
      2. 情報の利用目的<br />
      収集した情報は、サイトの改善、コンテンツの充実、およびユーザー体験の向上のために利用します。<br /><br />
      3. 第三者への提供<br />
      法令に基づく場合を除き、収集した個人情報を第三者に提供することはありません。<br /><br />
      4. Cookieの使用<br />
      当サイトでは、ユーザー体験向上のためにCookieを使用しています。ブラウザの設定でCookieを無効にすることも可能です。<br /><br />
      5. 広告について<br />
      当サイトではGoogle AdSenseを利用しており、ユーザーの興味に基づいた広告が表示されることがあります。<br /><br />
      ホームに戻る
    </>
  );

  const ownerInfo = (
    <>
      氏名<br />
      黒井葉跡<br /><br />
      サイトの目的<br />
      知り合いに、いくつかの恐怖症を持っている方がおり、事前に知らずに映画を観て不愉快な思いを経験をしたことがあったそうです。安心して映画を選べる環境を作りたいという想いからこのサイトを立ち上げました。皆様からの情報提供により、より多くの方の映画鑑賞をサポートできれば幸いです。<br /><br />
      連絡先<br />
      お問い合わせはこちらのフォーム（お問い合わせフォームと同じアドレス）からお願いします。
    </>
  );

  const guidelines = (
    <>
      投稿の目的<br />
      恐怖症を持つ方が安心して映画を選べるよう、正確で役立つ情報の提供にご協力ください。<br /><br />

      投稿時の注意点<br />
      ・実際に視聴した映画についてのみ投稿してください。<br />
      ・恐怖要素の詳細は具体的に記入してください（例：「高所シーンあり」ではなく「30分頃、高いビルの屋上からの視点で撮影されたシーンが約2分間続く」など）。<br />
      ・ネタバレになる可能性がある場合は、その旨を明記してください。<br />
      ・出現時間は分かる範囲で記入してください（任意）。<br /><br />

      禁止事項<br />
      ・虚偽の情報の投稿<br />
      ・映画の内容と関係のない投稿<br />
      ・誹謗中傷や差別的な表現を含む投稿<br />
      ・著作権を侵害する内容の投稿<br />
      ・広告や宣伝目的の投稿
    </>
  );

  const faq = (
    <>
      Q: このサイトはどのように使えばいいですか？<br />
      A: 映画のタイトルを検索するか、「公開予定の映画」「上映中の映画」などのカテゴリから映画を選び、詳細ページで恐怖要素を確認できます。<br /><br />

      Q: 恐怖要素の情報はどこから来ていますか？<br />
      A: サイト利用者からの投稿情報です。より多くの方の協力で情報の質と量を高めていきます。<br /><br />

      Q: 映画の恐怖要素情報を追加するにはどうすればいいですか？<br />
      A: 映画の詳細ページにある「恐怖要素を追加」ボタンから投稿できます。<br /><br />

      Q: 間違った情報を見つけた場合はどうすればいいですか？<br />
      A: お問い合わせフォームから報告してください。確認の上、修正対応いたします。<br /><br />

      Q: 探している映画が見つからない場合は？<br />
      A: 正確なタイトルで検索してみてください。それでも見つからない場合は、TMDBのデータベースにまだ登録されていない可能性があります。
    </>
  );

  return (
    <footer className={styles.footer}>
      <nav className={styles.nav}>
        <a href="/">ホーム</a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("サイト説明", siteDescription);
          }}
        >
          サイト説明
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("免責事項", disclaimer);
          }}
        >
          免責事項
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("プライバシーポリシー", privacyPolicy);
          }}
        >
          プライバシーポリシー
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("運営者情報", ownerInfo);
          }}
        >
          運営者情報
        </a>
        <a
          href="https://same-sunflower-d44.notion.site/20d0d0e1f57b80bd9af2c6e925aa0c20"
          target="_blank"
          rel="noopener noreferrer"
        >
          お問い合わせ
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("投稿ガイドライン", guidelines);
          }}
        >
          投稿ガイドライン
        </a>
        <a
          href="/"
          onClick={(e) => {
            e.preventDefault();
            openModal("FAQ", faq);
          }}
        >
          FAQ
        </a>
      </nav>

      <div className={styles.copyRight}>
        <small>© 2023 ファビアチェック All Rights Reserved.</small>
        <br />
        <small>
          This product uses the TMDB API but is not endorsed or certified by TMDB.
        </small>
      </div>

      {/* TMDBロゴ部分は必要に応じてここに入れてください */}

      <Modal isOpen={modalOpen} onClose={closeModal} title={modalContent.title}>
        <div style={{ textAlign: "left" }}>
    {modalContent.body}
      </div>
        {modalContent.body}
      </Modal>
    </footer>
  );
};

export default Footer;
