import React, { useEffect, useState } from "react";

const MovieList = ({ category, query }) => {
  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8); // ✅ 最初の表示数
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  const fetchMovies = async () => {
    console.log("選択されたカテゴリー:", category); // ✅ カテゴリー確認

    let url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`
      : `https://api.themoviedb.org/3/movie/${category}?api_key=${API_KEY}&language=ja-JP`;

    console.log("リクエストURL:", url); // ✅ APIに送るURL確認

    try {
      const response = await fetch(url);

      if (!response.ok) {
        console.error("APIリクエスト失敗: ステータス", response.status);
        setMovies([]);
        return;
      }

      const data = await response.json();
      console.log("APIレスポンス:", data);

      if (!data.results || data.results.length === 0) {
        console.error("APIレスポンスの形式が異常です:", data);
        setMovies([]);
        return;
      }

      setMovies(data.results);
    } catch (error) {
      console.error("映画データの取得に失敗しました:", error);
      setMovies([]);
    }
  };

  useEffect(() => {
  fetchMovies();
}, [category, query]);

console.log("取得した映画データ:", movies); // ✅ useEffect の外に移動
  return (
    <div className="movie-list">
      {movies.length > 0 ? (
        movies.slice(0, visibleCount).map((movie) => (
          <div key={movie.id} className="movie-card">
            <img src={movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` : "代替画像URL"} alt={movie.title} />
            <h3>{movie.title}</h3>
            <p>⭐ {movie.vote_average}</p>
          </div>
        ))
      ) : (
        <p>映画データがありません</p> // ✅ データがない場合のメッセージ
      )}

      {visibleCount < movies.length && (
        <button className="load-more" onClick={() => setVisibleCount(visibleCount + 8)}>
          もっと見る
        </button>
      )}
    </div>
  );
};

export default MovieList;
