import React, { useState } from "react";

const scaryTypes = [
  "高所","閉所","暗所","蜘蛛","蝶","蟻","昆虫","蛙","ヘビ","集合体",
  "血液描写","水・溺水","雷","嘔吐","注射・先端","ピエロ","幽霊・超常現象",
  "性的シーン","死体","その他（自由記述）"
];

const ScaryForm = ({ onAdd }) => {
  const [selectedTypes, setSelectedTypes] = useState([]);
  const [detail, setDetail] = useState("");
  const [time, setTime] = useState("");

  // チェックボックスの変更処理
  const handleCheckboxChange = (type) => {
    if (selectedTypes.includes(type)) {
      setSelectedTypes(selectedTypes.filter(t => t !== type));
    } else {
      setSelectedTypes([...selectedTypes, type]);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedTypes.length === 0) {
      alert("少なくとも1つの恐怖要素を選択してください");
      return;
    }
    if (!detail.trim()) {
      alert("詳細は必須です");
      return;
    }
    // まとめて1つの投稿として送る形にするならこうする
    onAdd({ types: selectedTypes, detail, time });
    setSelectedTypes([]);
    setDetail("");
    setTime("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <fieldset>
        <legend>恐怖要素（複数選択可）</legend>
        {scaryTypes.map(type => (
          <label key={type} style={{ display: "block" }}>
            <input
              type="checkbox"
              value={type}
              checked={selectedTypes.includes(type)}
              onChange={() => handleCheckboxChange(type)}
            />
            {type}
          </label>
        ))}
      </fieldset>
      <textarea
        placeholder="詳細"
        value={detail}
        onChange={(e) => setDetail(e.target.value)}
        required
      />
      <input
        placeholder="出現時間 (任意)"
        value={time}
        onChange={(e) => setTime(e.target.value)}
      />
      <button type="submit">送信</button>
    </form>
  );
};

export default ScaryForm;
