import React, { useEffect, useState, useCallback } from "react";
import styles from "./MovieList.module.css";

const MovieList = ({ category, query }) => {
  console.log("üì¶ MovieList „Ç≥„É≥„Éù„Éº„Éç„É≥„Éà„ÅåÂëº„Å∞„Çå„Åæ„Åó„ÅüÔºÅ");

  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8);
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  // ‚úÖ `category` „Åå `undefined` „ÅÆÂ†¥Âêà„ÅÆ„Éá„Éï„Ç©„É´„ÉàÂÄ§„ÇíË®≠ÂÆö
  const validatedCategory = category || "popular";

  // ‚úÖ `fetchMovies` „Çí `useCallback` „Åß„É°„É¢Âåñ„Åó„Å¶ `useEffect` „ÅÆË≠¶Âëä„ÇíËß£Ê∂à
  const fetchMovies = useCallback(async () => {
    console.log("üé¨ fetchMovies() „ÅåÂÆüË°å„Åï„Çå„Åæ„Åó„ÅüÔºÅ");
    console.log("üîç category:", validatedCategory, "query:", query);
    console.log("üîë API_KEY:", API_KEY);

    if (!API_KEY) {
      console.error("‚ùå API_KEY „ÅåÂÆöÁæ©„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„ÇìÔºÅ");
      return setMovies([]);
    }

    const url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`
      : `https://api.themoviedb.org/3/movie/${validatedCategory}?api_key=${API_KEY}&language=ja-JP`;

    console.log("üåç „É™„ÇØ„Ç®„Çπ„ÉàURL:", url);

    try {
      const response = await fetch(url);
      if (!response.ok) {
        console.error(`‚ùå API„É™„ÇØ„Ç®„Çπ„ÉàÂ§±Êïó: „Çπ„ÉÜ„Éº„Çø„Çπ„Ç≥„Éº„Éâ ${response.status}`);
        setMovies([]);
        return;
      }

      const data = await response.json();
      console.log("‚úÖ API„É¨„Çπ„Éù„É≥„Çπ:", data);

      // ‚úÖ `data.results` „ÅåÈÖçÂàó„Åã„Å©„ÅÜ„Åã„Çí‰øùË®º
      setMovies(Array.isArray(data?.results) ? data.results : []);
    } catch (error) {
      console.error("‚ö†Ô∏è Êò†Áîª„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
      setMovies([]);
    }
  }, [validatedCategory, query, API_KEY]);

  useEffect(() => {
    console.log("üëÄ useEffect Áô∫ÂãïÔºÅ");
    fetchMovies();
  }, [fetchMovies]);

  console.log("üéûÔ∏è ÁèæÂú®„ÅÆ movies:", movies);

  return (
    <div className="movie-list">
      {Array.isArray(movies) && movies.length > 0 ? (
        <>
          {movies.slice(0, visibleCount).map((movie) => (
            <div key={movie.id} className={styles.card}>
              <img
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "https://via.placeholder.com/500"
                }
                alt={movie.title || "Êò†Áîª„Çø„Ç§„Éà„É´Êú™Ë®≠ÂÆö"}
              />
              <h3>{movie.title || "„Çø„Ç§„Éà„É´‰∏çÊòé"}</h3>
              <p>‚≠ê {movie.vote_average ?? "Ë©ï‰æ°„Å™„Åó"}</p>
            </div>
          ))}
          {visibleCount < movies.length && (
            <button className="load-more" onClick={() => setVisibleCount(visibleCount + 8)}>
              „ÇÇ„Å£„Å®Ë¶ã„Çã
            </button>
          )}
        </>
      ) : (
        <p style={{ color: "red" }}>‚ö†Ô∏è Êò†Áîª„Éá„Éº„Çø„ÅåÂèñÂæó„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇ</p>
      )}
    </div>
  );
};

export default MovieList; /* ‚úÖ „Åì„Çå„ÇíËøΩÂä†ÔºÅ */
