import React, { useEffect, useState } from "react";

const MovieList = ({ category, query }) => {
  const [movies, setMovies] = useState([]);
  const [visibleCount, setVisibleCount] = useState(8); // 最初の表示数
  const API_KEY = process.env.REACT_APP_TMDB_API_KEY;

  const fetchMovies = async () => {
    let url = query
      ? `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=ja-JP&query=${query}`
      : `https://api.themoviedb.org/3/movie/${category}?api_key=${API_KEY}&language=ja-JP`;

    console.log("リクエストURL:", url); // ✅ URL確認用ログ

    try {
      const response = await fetch(url);
      const data = await response.json();
      console.log("APIレスポンス:", data); // ✅ APIレスポンス確認用ログ
      setMovies(data.results);
    } catch (error) {
      console.error("映画データの取得に失敗しました", error);
    }
  };

  useEffect(() => {
    fetchMovies(); // ✅ APIリクエストを実行
  }, [category, query]);

  return (
    <div className="movie-list">
      {movies.map((movie) => (
        <div key={movie.id} className="movie-card">
          <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
          <h3>{movie.title}</h3>
          <p>⭐ {movie.vote_average}</p>
        </div>
      ))}
    </div>
  );
};

export default MovieList;
