import React, { useState, useEffect } from "react";
import Modal from "./Modal";
import ScaryForm from "./ScaryForm";

const API_KEY = "9c5b6fe18f36543b858effdaf87e44e0";

const MoviePoster = ({ movieId, posterUrl, title }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [movieDetails, setMovieDetails] = useState(null);
  const [scaryItems, setScaryItems] = useState([]);

  // モーダルを開いたら映画詳細を取得
  useEffect(() => {
    if (!modalOpen) return;

    const fetchMovieDetails = async () => {
      try {
        const res = await fetch(
          `https://api.themoviedb.org/3/movie/${movieId}?api_key=${API_KEY}&language=ja-JP`
        );
        const data = await res.json();
        setMovieDetails(data);
      } catch (e) {
        console.error("映画詳細の取得に失敗しました", e);
      }
    };
    fetchMovieDetails();
  }, [modalOpen, movieId]);

  const handleAddScaryItem = (newItem) => {
    setScaryItems([...scaryItems, newItem]);
  };

  return (
    <>
      <img
        src={posterUrl}
        alt={title}
        style={{ cursor: "pointer", maxWidth: "200px" }}
        onClick={() => setModalOpen(true)}
      />
      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)} title={title}>
        {!movieDetails ? (
          <p>読み込み中...</p>
        ) : (
          <>
            <div style={{ display: "flex", gap: "20px" }}>
              <img
                src={
                  movieDetails.poster_path
                    ? `https://image.tmdb.org/t/p/w300${movieDetails.poster_path}`
                    : "https://via.placeholder.com/300x450?text=No+Image"
                }
                alt={movieDetails.title}
                style={{ borderRadius: "8px" }}
              />
              <div>
                <p><strong>公開日:</strong> {movieDetails.release_date || "不明"}</p>
                <p><strong>ジャンル:</strong> {movieDetails.genres.map(g => g.name).join(", ") || "なし"}</p>
                <p><strong>評価:</strong> {movieDetails.vote_average || "なし"}</p>
                <p><strong>あらすじ:</strong> {movieDetails.overview || "なし"}</p>
              </div>
            </div>

            <h3>登録された恐怖要素</h3>
            {scaryItems.length === 0 ? (
              <p>登録なし</p>
            ) : (
              <ul>
                {scaryItems.map((item, i) => (
                  <li key={i}>
                    {item.types.join(", ")} — {item.detail} {item.time && `(${item.time})`}
                  </li>
                ))}
              </ul>
            )}

            <ScaryForm onAdd={handleAddScaryItem} />
          </>
        )}
      </Modal>
    </>
  );
};

export default MoviePoster;
